%Read and display dubins path generated by the C++ program.
function [] = dubins_results(pathfile, pointsfile)  
%     load ../out/path.txt;       %  read data into the path matrix
%     x = path(:,1);             %  copy first column of path into x
%     y = path(:,2);             %  and second column into y

    if nargin < 2
        pathfile = '../out/path.txt';
        pointsfile = '../out/points.txt';
    end;

    fpath = fopen(pathfile, 'r');
    len = fscanf(fpath, 'len=%d\n', 1);
    fprintf(1, 'len=%d\n', len);

    rows = 3;
    cols = len;
    path = fscanf(fpath, '%f', [rows, cols]);
    fclose(fpath);
    %path = path';
    x = path(1,:);             
    y = path(2,:);  
    
    fid = fopen(pointsfile);
    type = fscanf(fid, 'type=%d\n', 1);
    gamma1 = fscanf(fid, 'gamma1=%f\n', 1);
    gamma3 = fscanf(fid, 'gamma3=%f\n', 1);
    sgn1 = fscanf(fid, 'sgn1=%d\n', 1);
    sgn3 = fscanf(fid, 'sgn3=%d\n', 1);
    s1 = fscanf(fid, 's1=%f\n', 1);
    s2 = fscanf(fid, 's2=%f\n', 1);
    s3 = fscanf(fid, 's3=%f\n', 1);
    H = fscanf(fid, 'H=%f\n', 1);
    fprintf(1, 'type=%d gamma1=%f gamma3=%f sgn1=%d sgn3=%d s1=%f s2=%f s3=%f H=%f\n', type, gamma1, gamma3, sgn1, sgn3, s1, s2, s3, H);
    
    rows = 3;
    cols = 4;
    xy = fscanf(fid, '%f', [rows, cols]);
    fclose(fid);
    
    a = xy(:,1);
    q1 = xy(:,2);
    q2 = xy(:,3);
    b = xy(:,4);
    plot_dubins(a, b, q1, q2, x, y, H, gamma1, gamma3, sgn1, sgn3, type, s1, s3);
end